import { useState} from 'react'

function Cart({cart, updateCart}) {
    const monsteraPrice = 8    
    const [isOpen, setIsOpen] = useState(false)

    return (isOpen ? (
        <div className="lmj-cart">
            <button className="lmj-cart-toggle-button" onClick={()=>setIsOpen(false)}>Fermer</button>
            {}
            <h2>Panier</h2>           
            <h3> Total : {monsteraPrice * cart}â‚¬</h3>
            <button className="lmj-cart-toggle-button" onClick={()=>{updateCart(0)}}>Vider le panier</button>
        </div>
        ) : (
            <div className="lmj-cart-closed">
                <button className="lmj-cart-toggle-button" onClick={()=>setIsOpen(true)}>Ouvri le panier</button>
            </div>
        )        
        )
    }

export default Cart;
