import { useState } from "react/cjs/react.development"
import { plantList } from "../datas/plantList"


function Categories({categorie, updateCategorie}){
    const category = plantList.reduce(
        (acc, plant) => acc.includes(plant.category) ? acc : acc.concat(plant.category),
        []
    )    

    return(
        <div>
            <label htmlFor="categories">Choisisez une catégories</label>
            <select name="categories">
                <option key="" value="" onClick={()=>updateCategorie('all')} selected={'all' === categorie ? true : false}>Toutes</option>
                {
                    category.map((cat)=>(
                        <option key={cat} value={cat} onClick={()=>updateCategorie({cat})} selected={cat === categorie.cat ? true : false}>{cat}</option>
                    ))
                }
            </select>
            <button onClick={()=>updateCategorie("all")}>Réinitialiser</button>
        </div>      
    )
}

export default Categories
