import { PureComponent } from 'react'
import sun from '../assets/sun.svg'
import water from '../assets/water.svg'

function CareScale({scaleValue, careType}){
    const scaleType = careType === 'light' ? (<img src={sun} alt='sun icon' />) : (<img src={water} alt='water icon' />)
    const range = [1,2,3]

    return <div >
        {range.map((rangeElement)=>
        console.log(scaleType)
            // scaleValue >= rangeElement ? <span key={rangeElement.toString()} onClick={()=>{handleClick(careType, scaleType)}}>{scaleType}</span> : null
        )}
    </div>
}

function handleClick(careType, scaleType){    
    const need = careType === 'light' ? 'éclairage' : 'arrossage'
    const requirement = scaleType
    switch (requirement) {
        case 1 : 'peu'; break;
        case 2 : 'modérement'; break;
        case 3 : 'beacoup'; break;
    }
    alert(`Ce composant est de type ${careType} et la plante à besoin de ${requirement} d'${need}`)
}

export default CareScale
